import React, { useState } from 'react';
import { useNavigation } from './NavigationContext';
import SearchHeader from './SearchHeader';
import FilterBar from './FilterBar';
import PropertyCard from './PropertyCard';
import ReviewsModal from './ReviewsModal';
import LocationModal from './LocationModal';
import properties from '../mock/properties';
import ComparisonModal from './ComparisonModal';
import { useComparison } from './ComparisonContext';


const HomeScreen = () => {
  const [filteredProperties, setFilteredProperties] = useState(properties);
  const [selectedProperty, setSelectedProperty] = useState(null);
  const [showReviews, setShowReviews] = useState(false);
  const [showMap, setShowMap] = useState(false);
  const { setCurrentView, setCurrentProperty } = useNavigation();
const { isModalOpen, setModalOpen, comparisonList } = useComparison();

  const handleFilter = (filter) => {
    let filtered = properties;
    if (filter === 'sale') {
      filtered = properties.filter(p => p.type === 'Venta');
    } else if (filter === 'rent') {
      filtered = properties.filter(p => p.type === 'Renta');
    } else if (filter === 'featured') {
      filtered = properties.filter(p => p.featured);
    }
    setFilteredProperties(filtered);
  };

  const handleSearch = (query) => {
    const filtered = properties.filter(p =>
      p.title.toLowerCase().includes(query.toLowerCase()) ||
      p.address.toLowerCase().includes(query.toLowerCase())
    );
    setFilteredProperties(filtered);
  };

  const handleShowReviews = (property) => {
    setSelectedProperty(property);
    setShowReviews(true);
  };

  const handleShowMap = (property) => {
    setSelectedProperty(property);
    setShowMap(true);
  };

  return (
    <>
      <SearchHeader />
      <div className="p-4">
        <FilterBar onFilter={handleFilter} onSearch={handleSearch} />
        <div className="space-y-4">
          {filteredProperties.map((property) => (
            <PropertyCard 
              key={property.id} 
              property={property}
              onClick={() => {
                setCurrentProperty(property);
                setCurrentView('property');
              }}
              onShowReviews={() => handleShowReviews(property)}
              onShowMap={() => handleShowMap(property)}
            />
          ))}
        </div>
      </div>

      {showReviews && selectedProperty && (
        <ReviewsModal 
          property={selectedProperty}
          onClose={() => setShowReviews(false)}
        />
      )}

      {showMap && selectedProperty && (
        <LocationModal
          property={selectedProperty}
          onClose={() => setShowMap(false)}
        />
      )}
    </>
  );
  {isModalOpen && (
  <ComparisonModal onClose={() => setModalOpen(false)} />
)}

};
<button
  onClick={() => {
    if (comparisonList.length >= 2) {
      setModalOpen(true);
    } else {
      alert('Selecciona al menos 2 propiedades para comparar');
    }
  }}
>
  Comparar
</button>

export default HomeScreen;

